import {useBlocks} from '@tinypixelco/wp-react-hooks'

const component = () => {
  const {blocks} = useBlocks()

  return (
    <ul>
      {blocks.map((block, id) => <li key={id}>{block.name}</li>}
    </ul>
  )
}

import {useBlocks} from '@tinypixelco/wp-react-hooks'

const component = () => {
  const {count} = useBlocks()

  return (
    <div>
      Blocks used: {count || null}
    </div>
  )
}

