import {useSelection} from '@tinypixelco/wp-react-hooks'
import {useEffect} from '@wordpress/element'

const component = () => {
  const [selection, setSelection] = useSettings()

  return (
    <div>
      {selection && JSON.stringify(selection)}
    </div>
  )
}

